const express = require('express');
const app = express();
// const port = 4000;
let a = 70;
let b = 50;
let c = 38;
let d ; 

app.get('/',(req,res)=>{
    res.send('welcome');
});
app.get('/sum',(req,res)=>{
    if(a>b && a>c){
        res.send(`a is ${a} greater than b:${b} & c:${c}`);
    }else if(b>a && b>c){
        res.send(`b is ${b} is greater than a:${a} & c:${c}`)
    }else{
        res.send(`c is ${c} is greater than a:${a} & b:${b}`)
    }
});

app.get('/multiplication',(req,res)=>{
    res.send(`d = ${a*b}`)
})
app.listen(9091,()=>{
    console.log(`multiplication of a & b`);
});

